$(function(){
	// Container
  var $container = $('.theme-colors'); 

  
  $(document).on('click', function(e) {
  	// If the button is clicked
    var $e = $(e.target);
    // If the element is a part of the button or the button is clicked.
    if ($e.parent('#slideBtn').length > 0 || $e.attr('id') === 'slideBtn') {
    	$container.toggleClass('shown');
    	return; 
    }
 
    // Any element outside the button.
  	$container.removeClass('shown');
  });
});
